import React, { useState } from 'react';
import axios from 'axios';
import './Dashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const Dashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleButtonClick = (type) => {
    // if (type === 'Feedback') {
    //   alert('Feedback button clicked!');
    // } else {
      axios.post('/api/generate-report', { type })
        .then(response => {
          alert(`Report generated successfully for ${type}!`);
        })
        .catch(error => {
          console.error('There was an error generating the report!', error);
        });
       //}
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className="admin-dashboard">
      <header className="header text-white p-3">
        <h1 className="m-0">TalesmithAI Admin</h1>
      </header>
      {!isSidebarOpen && (
        <div className="burger-icon" onClick={toggleSidebar}>
          &#9776;
        </div>
      )}
      {isSidebarOpen && (
        <div className={`sidebar p-3 ${isSidebarOpen ? 'open' : ''}`}>
          <h4>Generate Reports</h4>
          <div className="dropdown">
            <button 
              className="btn report-button w-100 mb-2 dropdown-toggle"
              onClick={toggleDropdown}
            >
              Users
            </button>
            {isDropdownOpen && (
              <div className="dropdown-menu show">
                <button className="dropdown-item" onClick={() => handleButtonClick('All')}>All</button>
                <button className="dropdown-item" onClick={() => handleButtonClick('Subscribed')}>Subscribed</button>
                <button className="dropdown-item" onClick={() => handleButtonClick('Unsubscribed')}>Unsubscribed</button>
              </div>
            )}
          </div>
          <button 
            className="btn report-button w-100 mt-2"
            // onClick={() => handleButtonClick('Feedback')}
          >
            Feedback
          </button>
          <div className="cross-icon" onClick={toggleSidebar}>
            &#10005;
          </div>
        </div>
      )}
      {/* <main className="main-content p-3">
        <div className="center-content">
          <div className="table-responsive table-container">
            <table className="table table-bordered table-hover">
              <thead className="thead-dark">
                <tr>
                  <th>Column 1</th>
                  <th>Column 2</th>
                  <th>Column 3</th>
                </tr>
              </thead>
            </table>
          </div>
        </div>
      </main> */}
    </div>
  );
};

export default Dashboard;
