// frontend/src/components/Header.jsx
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import Sidebar from './Sidebar';
import SearchBox from './SearchBox';
import './header.css';

const Header = ({ logout }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <div className={`header ${isSidebarOpen ? 'sidebar-open' : ''}`}>
            {/* Sidebar toggle button */}
            <div className="sidebar-toggle">
                <button className="toggle-btn" onClick={toggleSidebar}>
                    <FontAwesomeIcon icon={isSidebarOpen ? faTimes : faBars} />
                </button>
            </div>
            
            {/* Search box component */}
            <SearchBox />
            <div className="content centered-content col-mb-3-sm-4">
                <h3>Hey there, how can I help you today?</h3>
            </div>
            <div className="row no-gutters">
                <div className="col-sm-6">
                    <div className="card small-card">
                        <div className="card-body">
                            <h6 className="card-title">Write a story </h6>
                            <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
                        </div>
                    </div>
                </div>
                <div className="col-sm-6">
                    <div className="card small-card">
                        <div className="card-body">
                            <h5 className="card-title">Special title treatment</h5>
                            <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
                        </div>
                    </div>
                </div>
                <div className="col-sm-6">
                    <div className="card small-card">
                        <div className="card-body">
                            <h5 className="card-title">Special title treatment</h5>
                            <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
                        </div>
                    </div>
                </div>
                <div className="col-sm-6">
                    <div className="card small-card">
                        <div className="card-body">
                            <h5 className="card-title">Special title treatment</h5>
                            <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
                        </div>
                    </div>
                </div>
            </div>
            
            {/* Sidebar component */}
            <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} logout={logout} />
        </div>
    );
};

export default Header;
