import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { StoryContext } from './StoryContext';
import './Chat.css';

const Chat = () => {
  const { storyRequest, storyResponse, setStoryResponse } = useContext(StoryContext);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchStory = async () => {
      if (!storyRequest) return;
      setLoading(true);
      try {
        const response = await axios.post('YOUR_API_ENDPOINT', { query: storyRequest });
        setStoryResponse(response.data);
      } catch (error) {
        console.error('Error generating story:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStory();
  }, [storyRequest, setStoryResponse]);

  return (
    <div className="chat-page">
      {loading && <div className="loading">Generating your story...</div>}
      {storyResponse && (
        <div className="response-section">
          <p>{storyResponse.text}</p>
          {storyResponse.image && <img src={storyResponse.image} alt="Generated visual" />}
        </div>
      )}
    </div>
  );
};

export default Chat
