import React, { useState, useRef } from "react";
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
// import { IoSearch } from "react-icons/io5";
import 'bootstrap/dist/css/bootstrap.min.css';
import './header.css';  // Ensure the path is correct
import logo from "./assest/Tales-Logo.png";
import Sidebar from "./Sidebar";

const Header = ({ logout }) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const inputRef = useRef(null);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleSearchInputChange = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    navigate('/chat', { state: { query: searchQuery } });
  };

  const handleSuggestionClick = (suggestion) => {
    navigate('/chat', { state: { query: suggestion } });
  };

  return (
    <div className={`header-container ${isSidebarOpen ? 'header-sidebar-open' : ''}`}>
      {!isSidebarOpen && (
        <div className="header-sidebar-toggle">
          <button className="toggle-btn" onClick={toggleSidebar}>
            <FontAwesomeIcon icon={faBars} />
          </button>
        </div>
      )}

      <div className="header-content container">
        <div className="header-search-bar-container row justify-content-center">
          <div className="header-search-box col-12 col-md-8 col-lg-6">
            <form onSubmit={handleSearchSubmit} className="w-100 position-relative">
              <input
                ref={inputRef}
                value={searchQuery}
                onChange={handleSearchInputChange}
                type="search"
                name="search"
                id="search"
                placeholder="Message TalesMith AI"
                className="form-control"
              />
              {/* <button 
                type="submit" 
                className="header-search-icon"
                disabled={!searchQuery} // Disable the button when searchQuery is empty
              >
                <IoSearch />
              </button> */}
            </form>
          </div>
        </div>
        <div className="header-items-container mt-5">
          <div className="header-item-box text-center">
            <div className="msg-box">
              <h4>How Can TalesMithAI help you?</h4>
            </div>
            <div className="logo-box mt-3">
              <img id="tale-logo" className="header-tale-logo" src={logo} alt="logo" />
            </div>
          </div>
        </div>
        <div className="header-suggestion-container row mt-4">
          <div className="header-suggestion-box col-6 col-md-3" onClick={() => handleSuggestionClick("Write a Story about a Thirsty Crow")}>
            <div className="content">
              <p>
                hello
                <span className="below">ask me your questions</span>
              </p>
            </div>
          </div>
          <div className="header-suggestion-box col-6 col-md-3" onClick={() => handleSuggestionClick("Make a personal webpage after asking me three questions")}>
            <div className="content">
              <p>
                suggest a story
                <span className="below">
                  Fictional story 
                </span>
              </p>
            </div>
          </div>
          <div className="header-suggestion-box col-6 col-md-3" onClick={() => handleSuggestionClick("Design a programming game to teach basics in a fun way")}>
            <div className="content">
              <p>
                suggest a story
                <span className="below">helping others</span>
              </p>
            </div>
          </div>
          <div className="header-suggestion-box col-6 col-md-3" onClick={() => handleSuggestionClick("Help me pick an outfit that will look good")}>
            <div className="content">
              <p>
                write a story
                <span className="below">about honesty</span>
              </p>
            </div>
          </div>
        </div>
      </div>
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} logout={logout} />
    </div>
  );
};

export default Header;
