import React, { useEffect, useState, useCallback } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axiosInstance from './axiosSetup';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import Sidebar from './Sidebar';
import './Chat.css';
import './Sidebar.css';

const Chat = () => {
  const location = useLocation();
  const navigate = useNavigate();  // Updated to useNavigate
  const { query } = location.state || {};
  const [message, setMessage] = useState(query || '');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const fetchChatHistory = async () => {
    try {
      const response = await axiosInstance.get('/api/chat/history/');
      console.log('Chat History Response:', response.data); // Debug log
    // Assuming the response data directly contains the chat history
      setChatHistory(response.data);
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  const sendMessage = useCallback(async (messageToSend) => {
    setLoading(true);
    setChatHistory((prevHistory) => [...prevHistory, { sender: 'user', message: messageToSend }]);
    try {
      const response = await axiosInstance.post('/api/chat/', { message: messageToSend });
      setChatHistory((prevHistory) => [
        ...prevHistory,
        { sender: 'ai', message: response.data.response },
      ]);
      setMessage('');
    } catch (error) {
      console.error('Error generating response:', error);
      setChatHistory((prevHistory) => [
        ...prevHistory,
        { sender: 'ai', message: 'Error generating response. Please try again.' },
      ]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (query) {
      sendMessage(query);
    }
  }, [query, sendMessage]);

  const handleSendMessage = () => {
    if (message.trim()) {
      sendMessage(message);
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleNewChat = () => {
    setChatHistory([]);
    setMessage('');
    fetchChatHistory();
  };

  const handleLogout = async () => {
    try {
      await axiosInstance.post('/api/logout/');
      navigate('/login');  // Updated to use navigate
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="chat-page">
      <Sidebar
        isOpen={isSidebarOpen}
        toggleSidebar={toggleSidebar}
        newChat={handleNewChat}
        handleLogout={handleLogout}
      />
      <div className={`chat-content ${isSidebarOpen ? 'sidebar-open' : ''}`}>
        <button className="toggle-btn" onClick={toggleSidebar}>
          <FontAwesomeIcon icon={isSidebarOpen ? faTimes : faBars} />
        </button>
        <div className="chat-history">
          {chatHistory.map((chat, index) => (
            <div key={index} className={`chat-message ${chat.sender}`}>
              <span>{chat.sender === 'user' ? 'You: ' : 'AI: '}</span>{chat.message}
            </div>
          ))}
        </div>
        {loading && <div className="loading">Generating your response...</div>}
        <div className="chat-input">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type your message..."
            disabled={loading}
          />
          <button onClick={handleSendMessage} disabled={loading}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
