import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './MainComponent.css';

const MainComponent = () => {
  const [welcomeText, setWelcomeText] = useState('');
  const [animationComplete, setAnimationComplete] = useState(false);
  const [buttonsVisible, setButtonsVisible] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const text = "Welcome to TalesMithAI";

    const animateText = (text, setText) => {
      let i = 0;
      return new Promise((resolve) => {
        const interval = setInterval(() => {
          setText(text.substring(0, i + 1));
          i++;
          if (i === text.length) {
            clearInterval(interval);
            resolve();
          }
        }, 100);
      });
    };

    animateText(text, setWelcomeText).then(() => {
      setAnimationComplete(true);
      setButtonsVisible(true);
    });

  }, []);

  const handleLoginClick = () => {
    navigate('/login');
  };

  const handleSignupClick = () => {
    navigate('/signup');
  };

  return (
    <> 
    <div className="main-container">
      <div className='content-container'>
        <div className='text-container'>
        {animationComplete && (
            <h1 style={{ marginBottom: '20px' }}>{welcomeText}</h1>
          )}
        </div>
      </div>
    </div>
    <div className='button-container'>
    <h2 className="description"> Get Started</h2>
    <div className="buttons-wrapper" style={{ visibility: buttonsVisible ? 'visible' : 'hidden' }}>
            <button className="login-button" onClick={handleLoginClick}>
              Login
            </button>
            <button className="signup-button" onClick={handleSignupClick}>
              Signup
            </button>
          </div>

    </div>

    </>
  )

   
};

export default MainComponent;
