import React from 'react';
import './App.css';
import MainComponent from './MainComponent';
import Signup from './Signup';
import Login from './Login';
import Header from './Header';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Routes, Route } from 'react-router-dom';
import Chat from './Chat';
import Feedback from './Feedback';
import Payment from './Payment';
import PaymentForm from './PaymentForm';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';

// No need to import Header here
const stripePromise = loadStripe('pk_test_51POOuLRwnPTAWSL7K2FYCEekuODOfbTlAwX3a402BHhVvnzAaoHUYvahLa8ly0dvfYT0CjuNA2ceX56keiTwiBiR00GYhFXIus');

const App = () => {
  return (
    <div className="app">
      <Routes>
        <Route path="/" element={<MainComponent />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        {/* Keep the Header inside a Route to ensure it's only displayed on /mainapp */}
        <Route path="/mainapp" element={<Header />} />
        <Route path="/chat" element={<Chat />} /> 
        <Route path="/feedback" element={<Feedback />} />
        <Route path="/payment" element={<Payment />} />
        <Elements stripe={stripePromise}>
            <PaymentForm />
        </Elements>
      </Routes>
   </div>
  );
};

export default App;
