import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axiosInstance from './axiosSetup';
import './Chat.css';

const Chat = () => {
  const location = useLocation();
  const { query } = location.state || {};
  const [message, setMessage] = useState(query || '');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (query) {
      sendMessage(query);
    }
  }, [query]);

  const sendMessage = async (messageToSend) => {
    setLoading(true);
    setChatHistory((prevHistory) => [...prevHistory, { sender: 'user', message: messageToSend }]);
    try {
      const response = await axiosInstance.post('/api/chat/', { message: messageToSend });
      setChatHistory((prevHistory) => [
        ...prevHistory,
        { sender: 'ai', message: response.data.choices[0].message.content },
      ]);
      setMessage('');
    } catch (error) {
      console.error('Error generating story:', error);
      setChatHistory((prevHistory) => [
        ...prevHistory,
        { sender: 'ai', message: 'Error generating response. Please try again.' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      sendMessage(message);
    }
  };

  return (
    <div className="chat-page">
      <div className="chat-history">
        {chatHistory.map((chat, index) => (
          <div key={index} className={`chat-message ${chat.sender}`}>
            <span>{chat.sender === 'user' ? 'You: ' : 'AI: '}</span>{chat.message}
          </div>
        ))}
      </div>
      {loading && <div className="loading">Generating your story...</div>}
      <div className="chat-input">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          disabled={loading}
        />
        <button onClick={handleSendMessage} disabled={loading}>
          Send
        </button>
      </div>
    </div>
  );
};

export default Chat;
