// frontend/src/components/SearchBox.jsx
import React, { useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import axiosInstance from './axiosSetup';
import './SearchBox.css';

const SearchBox = ({ onSearch }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [response, setResponse] = useState('');
    const inputRef = useRef(null);

    const handleSearchInputChange = (event) => {
        const query = event.target.value;
        setSearchQuery(query);
        // Dynamically adjust input height based on scroll height
        inputRef.current.style.height = 'auto';
        inputRef.current.style.height = inputRef.current.scrollHeight + 'px';
    };

    const handleSearch = async () => {
        try {
            const res = await axiosInstance.post('/api/chat/', { message: searchQuery });
            setResponse(res.data.choices[0].message.content);
        } catch (error) {
            console.error('Error fetching response:', error);
        }
    };

    return (
        <div className="search-box-container">
            <div className="search-box-wrapper">
                <textarea
                    ref={inputRef}
                    value={searchQuery}
                    onChange={handleSearchInputChange}
                    className="search-input"
                    placeholder="Start Chat Here..."
                    style={{ width: '700px' }} // Adjust width as needed
                />
                <button onClick={handleSearch} className="search-button">
                    <FontAwesomeIcon icon={faSearch} className="search-icon" />
                </button>
            </div>
            {response && (
                <div className="response-box">
                    <p>{response}</p>
                </div>
            )}
        </div>
    );
};

export default SearchBox;
