import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axiosInstance from './axiosSetup'; // Import axiosInstance from axiosSetup.js

const Dashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [reportType, setReportType] = useState('');
  const [users, setUsers] = useState([]);
  

  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const response = await axiosInstance.get('/api/is_admin/');
        setIsAdmin(response.data.is_admin);
      } catch (error) {
        console.error('Error checking admin status:', error);
      }
    };
    checkAdmin();
  }, []);

  useEffect(() => {
    if (reportType) {
      handleGenerateReport();
    }
  }, [reportType]);

  const handleGenerateReport = async () => {
    try {
      const response = await axiosInstance.get(`/api/user_report/?type=${reportType}`);
      setUsers(response.data.users);
    } catch (error) {
      console.error('Error generating report:', error);
    }
  };

  return (
    <div className="admin-dashboard">
      {isAdmin && (
        <>
          <header className="header text-white p-3">
            <h1 className="m-0">TalesmithAI Admin</h1>
          </header>
          <div className="container-fluid">
            <div className="row">
              <div className="col-3">
                <button className="btn btn-primary" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
                  Toggle Sidebar
                </button>
                <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
                  <ul className="list-unstyled">
                    <li>
                      <button onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                        Reports
                      </button>
                      {isDropdownOpen && (
                        <ul>
                          <li onClick={() => setReportType('Subscribed')}>Subscribed Users</li>
                          <li onClick={() => setReportType('Unsubscribed')}>Unsubscribed Users</li>
                          <li onClick={() => setReportType('All')}>All Users</li>
                        </ul>
                      )}
                    </li>
                  </ul>
                </div>
              </div>
              <div className="col-9">
                <button className="btn btn-secondary" onClick={handleGenerateReport}>
                  Generate Report
                </button>
                <div className="report">
                  <h2>{reportType} Users</h2>
                  <ul className="list-unstyled">
                    {users.map((user, index) => (
                      <li key={index}>
                        <p>Email: {user.email}</p>
                        <p>Username: {user.username}</p>
                        <p>Active: {user.is_active ? 'Yes' : 'No'}</p>
                        <p>Joined: {new Date(user.timestamp).toLocaleDateString()}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
