import React, { useState, useEffect } from 'react';
import axiosInstance from './axiosSetup';
import { useNavigate } from 'react-router-dom';
import './Payment.css';

const Payment = () => {
    const [loading, setLoading] = useState(false);
    const [hasPaid, setHasPaid] = useState(null);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPaymentStatus = async () => {
            try {
                // Get CSRF token
                const csrfResponse = await axiosInstance.get('/api/get_csrf/');
                axiosInstance.defaults.headers['X-CSRFToken'] = csrfResponse.data.csrfToken;

                // Fetch payment status
                const response = await axiosInstance.get('/api/get_payment_status/');
                setHasPaid(response.data.has_paid);
            } catch (err) {
                console.error('Error fetching payment status:', err);
                setError('Error fetching payment status.');
            }
        };

        fetchPaymentStatus();
    }, []);

    const handlePayment = async () => {
        setLoading(true);
        try {
            const response = await axiosInstance.post('/api/create-checkout-session/');
            const sessionId = response.data.id;
            const stripe = window.Stripe('pk_test_51POOuLRwnPTAWSL7K2FYCEekuODOfbTlAwX3a402BHhVvnzAaoHUYvahLa8ly0dvfYT0CjuNA2ceX56keiTwiBiR00GYhFXIus');
            await stripe.redirectToCheckout({ sessionId });
        } catch (error) {
            console.error('Error creating checkout session:', error);
        } finally {
            setLoading(false);
            navigate('/paymentform');
        }
    };

    if (hasPaid === null) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <div className="payment-container">
                {hasPaid ? (
                    <p>You have already paid. No further action is required.</p>
                ) : (
                    <>
                        <h2>Payment Required</h2>
                        <p>You have used your free trial period. Please proceed to payment to continue.</p>
                        <button onClick={handlePayment} disabled={loading}>
                            {loading ? 'Processing...' : 'Proceed to Payment'}
                        </button>
                    </>
                )}
            </div>
        </div>
    );
};

export default Payment;
