// frontend/src/Chat.jsx

import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axiosInstance from './axiosSetup';
import './Chat.css';

const Chat = () => {
  const location = useLocation();
  const { query } = location.state || {};
  const [storyResponse, setStoryResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchStory = async () => {
      if (!query) return;
      setLoading(true);
      try {
        const response = await axiosInstance.post('/api/chat/', { message: query });
        setStoryResponse(response.data);
      } catch (error) {
        console.error('Error generating story:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStory();
  }, [query]);

  return (
    <div className="chat-page">
      {loading && <div className="loading">Generating your story...</div>}
      {storyResponse && (
        <div className="response-section">
          <p>{storyResponse.choices[0].message.content}</p>
        </div>
      )}
    </div>
  );
};

export default Chat;
