// src/Sidebar.jsx

import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import axiosInstance from './axiosSetup';
import './Sidebar.css';

const Sidebar = ({ isOpen, toggleSidebar, newChat, handleLogout }) => {
  const [chatHistory, setChatHistory] = useState([]);

  useEffect(() => {
    fetchChatHistory();
  }, []);

  const fetchChatHistory = async () => {
    try {
      const response = await axiosInstance.get('/api/chat/history/');
      setChatHistory(response.data.history);
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  const handleFeedback = () => {
    window.location.href = '/feedback';
  };

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      <button className="toggle-btn" onClick={toggleSidebar}>
        <FontAwesomeIcon icon={isOpen ? faTimes : faBars} />
      </button>
      <button className="new-chat-btn" onClick={newChat}>
        New Chat
      </button>
      <div className="chat-history">
        <h2>Chat History</h2>
        {chatHistory.map((chat, index) => (
          <div key={index} className="chat-history-item">
            <p><strong>You:</strong> {chat.message}</p>
            <p><strong>AI:</strong> {chat.response}</p>
          </div>
        ))}
      </div>
      <div className="actions">
        <button className="feedback-btn" onClick={handleFeedback}>
          Feedback
        </button>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
